  $(document).ready(function(){

    console.log('hi');

      $("#slideCon").on('mousewheel', function(e){

        var $actSection = $('#slideCon').find('.on').index();
        var $pofolSection = $('#slideCon').children('div').length;

        if( e.deltaY < 0 ){
          // console.log($actSection);
          // console.log($pofolSection);
          //
          // scrollMainCon.scrollDown($actSection,$pofolSection);
          if($actSection + 1 === $pofolSection){
            alert('마지막 프로젝트 입니다.');
          }else{
            console.log('프로젝트 : ' + $actSection);

            $('#slideCon').children('div').removeClass('on');
            $('#slideCon').children('div').find('button').removeClass('act');

            $('#slideCon').children('div').eq($actSection + 1).addClass('on').css({ left: '100%'});
            $('#slideCon').children('div').stop().eq($actSection).animate({left: '-100%'}, 500);
            $('#slideCon').children('div').stop().eq($actSection + 1).animate({left: 0}, 500, function(){
              $(this).find('button').addClass('act');
            });
          }

        }else{
          scrollMainCon.scrollUp($actSection,$pofolSection);
        }
      });
  });

  var scrollMainCon = {

    scrollDown : function(index,lastI){

      if(index + 1 === lastI){
        alert('마지막 프로젝트 입니다.');

      }else{
        console.log('프로젝트 : ' + index);

        $('#slideCon').children('div').removeClass('on');
        $('#slideCon').children('div').find('button').removeClass('act');

        $('#slideCon').children('div').eq(index + 1).addClass('on').css({ left: '100%'});
        $('#slideCon').children('div').stop().eq(index).animate({left: '-100%'}, 500);
        $('#slideCon').children('div').stop().eq(index + 1).animate({left: 0}, 500, function(){
          $(this).find('button').addClass('act');
        });
      }
    },

    scrollUp : function(index,lastI){

      if(index === 0){
        alert('첫 프로젝트 입니다.');

      }else{
        console.log('프로젝트 : ' + index);

        $('#slideCon').children('div').removeClass('on');        
        $('#slideCon').children('div').find('button').removeClass('act');

        $('#slideCon').children('div').eq(index - 1).addClass('on').css({ left: '-100%'});
        $('#slideCon').children('div').stop().eq(index).animate({left: '100%'}, 500);
        $('#slideCon').children('div').stop().eq(index - 1).animate({left: 0}, 500, function(){
          $(this).find('button').addClass('act');
        });
      }
    }
  }
